const {createCanvas, loadImage} = require("canvas");
const fs = require("node:fs/promises");

(async () => {
    // sample (very small) base64 image
    const base64Data = '';

    const b64 = 'data:image/png;base64,' + base64Data;
    const img = await loadImage(b64);
    const canvas = createCanvas(img.width, 480);
    const ctx = canvas.getContext("2d");

    //Draw image rectangle size
    ctx.fillStyle = '#ffffff'
    ctx.fillRect(0, 0, 300, 480)

    //Draw Image
    ctx.drawImage(img, 0, 0);
    ctx.fillStyle = "#000000";

    //Draw Merchant Name
    ctx.font = '14pt Font RSU Bold';
    ctx.fillText("NEOBOX-TH", 15, 380);

    //Optional Merchant Info
    const merchantName = "Suphachai Pinhom".toUpperCase();
    ctx.font = '8.5pt HK Grotesk Regular';
    ctx.fillText(merchantName, 15, 400); 

    //Draw Money Pay
    ctx.font = '16pt Font RSU Bold';
    ctx.textAlign = 'right'
    ctx.fillText("200.00", 280, 380);

    //Draw Currency
    ctx.font = '7pt HK Grotesk Medium';
    ctx.fillText("บาท (THB)", 280, 400);

    //Footter 
    ctx.fillStyle = '#00427A'
    ctx.fillRect(0, 420, 300, 480)

    const footterText1 = "QR Payment ยินดีชำระทุกธนาคาร";
    const footterText2 = "สแกนง่าย จ่ายสะดวก";

    //Draw Currency
    ctx.font = '10pt HK Grotesk Bold';
    ctx.textAlign = 'center'
    ctx.fillStyle = '#ffffff'
    ctx.fillText(footterText1, 150, 445);
    ctx.fillText(footterText2, 150, 460);

    //Get Base64 Text
    //console.log(canvas.toDataURL().split(",")[1]);

    // just to show it worked, convert back to base64 and write to file
    await fs.writeFile("test.png", canvas.toDataURL().split(",")[1], "base64");
})();